mapper.select:
SELECT o.oid, o.create_time as createTime, o.expect_time as expectTime, o.detail, o.amount, order_type.type_name as typeName, order_type.form as form, order_type.bg_color as bgColor FROM `order` o LEFT JOIN order_type ON o.order_type_id = order_type.order_type_id WHERE o.status = 'D' AND o.xdr != #{uid}
AND o.order_type_id = #{orderTypeId}
ORDER BY o.create_time DESC

SELECT o.oid as orderNo, xdr_user.nickname as buyer, jdr_user.nickname as receiver, order_type.type_name as typeName, order_status.description as status, order_status.color, o.amount, o.rating, o.create_time as createTime, o.accept_time as acceptTime, o.expect_time as expectedDeliveryTime, o.complete_time as finishTime FROM `order` o LEFT JOIN user xdr_user ON o.xdr = xdr_user.uid LEFT JOIN user jdr_user ON o.jdr = jdr_user.uid LEFT JOIN order_type ON o.order_type_id = order_type.order_type_id LEFT JOIN order_status ON o.status = order_status.status
AND o.oid = #{oid}
AND (o.oid LIKE CONCAT('%', #{keyword}, '%') OR xdr_user.nickname LIKE CONCAT('%', #{keyword}, '%') OR jdr_user.nickname LIKE CONCAT('%', #{keyword}, '%') OR order_type.type_name LIKE CONCAT('%', #{keyword}, '%'))
AND order_status.description = #{status}
ORDER BY o.create_time DESC
