mapper.select:
SELECT o.oid, o.create_time as createTime, o.expect_time as expectTime, o.detail, o.amount, order_type.type_name as typeName, order_type.form as form, order_type.bg_color as bgColor FROM `order` o LEFT JOIN order_type ON o.order_type_id = order_type.order_type_id WHERE o.status = 'D' AND o.xdr != #{uid}
AND o.order_type_id = #{orderTypeId}
ORDER BY o.create_time DESC

SELECT o.oid, o.xdr, o.jdr, o.create_time AS createTime, o.expect_time AS expectTime, o.order_type_id AS orderTypeId, o.aid, o.detail, o.amount, o.accept_time AS acceptTime, o.complete_time AS completeTime, o.status, o.rating, order_status.description AS statusName, order_status.color AS statusColor, order_type.type_name AS typeName FROM `order` o LEFT JOIN order_status ON o.status = order_status.status LEFT JOIN order_type ON o.order_type_id = order_type.order_type_id
o.xdr = #{uid}
o.jdr = #{uid}
(o.xdr = #{uid} OR o.jdr = #{uid})
AND o.status = #{status}
AND o.order_type_id = #{type}
ORDER BY o.create_time DESC
